mapper.select:
SELECT * FROM T_Calendars WHERE team_id = #{teamId}
AND (
current_num < max_num AND date_time > SYSDATE
current_num >= max_num OR date_time <= SYSDATE
)
ORDER BY date_time ASC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*) FROM T_Calendars WHERE team_id = #{teamId}
AND (
current_num < max_num AND date_time > SYSDATE
current_num >= max_num OR date_time <= SYSDATE
)

mapper.update:
UPDATE t_calendars
SET current_num = current_num + #{delta}
WHERE id = #{id} AND team_id = #{teamId}

    AND current_num > 0


    AND current_num < max_num

mapper.select:
SELECT current_num, max_num
FROM t_calendars
WHERE id = #{id} AND team_id = #{teamId}
