mapper.select:
SELECT *
FROM TCalendar
WHERE teamId = #{teamId}


    AND (


                currentNum < maxNum AND dateTime > NOW()




                currentNum >= maxNum OR dateTime <= NOW()


    )


ORDER BY dateTime DESC

LIMIT #{start}, #{size}

SELECT COUNT(*) FROM TCalendar WHERE teamId = #{teamId}
AND (
currentNum < maxNum AND dateTime > NOW()
currentNum >= maxNum OR dateTime <= NOW()
)

mapper.update:
UPDATE t_calendars
SET current_num = current_num + #{delta}
WHERE id = #{id} AND team_id = #{teamId}

    AND current_num > 0


    AND current_num < max_num

mapper.select:
SELECT current_num, max_num
FROM t_calendars
WHERE id = #{id} AND team_id = #{teamId}
