mapper.update:
update users
set user_access_time = systimestamp
where user_id = #{userId}

update users
set img_id = #{imgId}
where user_id = #{userId}

update users 
set password = #{password}, nickname = #{nickname}, username = #{username}, gender = #{gender}, age = #{age},
        phonenumber = #{phonenumber}, residence = #{residence}, email = #{email} 
where user_id = #{userId}
